# pretpostavljamo  da je svaki sastojak koji je sa desne strane vec ubacen
for maybe_dish, ingredients in dish_ingredients.items():
    dish_individual = food_ontology[maybe_dish.replace(" ", "_").lower()]
    for ingredient in eval(ingredients):
        ingredient_individual = food_ontology[ingredient.replace(" ", "_").lower()]
        g.add((ingredient_individual, RDF.type, food_ontology.Ingredient))
        g.add((dish_individual, food_ontology.hasIngredient, ingredient_individual))

g.serialize(destination="files\\ingredients.rdf", format="xml")
